#define HTML "<!DOCTYPE html><html lang=\"en\" dir=\"ltr\"><head><meta charset=\"utf-8\"><title>Auto-Watering</title><style>        #button_style {            border: 1px solid red;        }</style><meta name=\"viewport\"        content=\"width=device-width, initial-scale=1.0, minimum-scale=1.0, maximum-scale=1.0, user-scalable=no\"><meta name=\"apple-touch-fullscreen\" content=\"YES\"><meta name=\"apple-mobile-web-app-capable\" content=\"yes\"></head><body><datalist id=\"timeInputs\"><option value=\"now\"></option><option value=\"today 09:00\"></option><option value=\"today 12:00\"></option><option value=\"today 15:00\"></option><option value=\"today 18:00\"></option><option value=\"today 21:00\"></option><option value=\"1 days later (09:00)\"></option><option value=\"2 days later (09:00)\"></option></datalist><datalist id=\"repeatInputs\"><option value=\"twice for 1 Day\"><option value=\"once for 1 Day\"><option value=\"once for 2 Day\"><option value=\"once for 3 Day\"><option value=\"once for 4 Day\"><option value=\"once for 5 Day\"><option value=\"once for 6 Day\"><option value=\"once for A week\"></datalist><div><div style=\"margin: 1em;\"><div style=\"margin-top: 1em;\">                WateringTime<input id=\"timeInput\" list=\"timeInputs\" /></div><div style=\"margin-top: 1em;\">                RepeatCount<input id=\"repeatInput\" list=\"repeatInputs\" /></div><button style=\"margin-top: 1em;\" id='confirmBtn' width=\"100\" height=\"60\" style=\"margin-right: 1em;\">confirm</button></div><div style=\"margin: 1em;\"><button id='openLightBtn' width=\"100\" height=\"60\" style=\"margin-right: 1em;\">openLight</button><button id='closeLightBtn' width=\"100\" height=\"60\">closeLight</button></div><div style=\"margin: 1em;\"><button id='openPumpBtn' width=\"100\" height=\"60\" style=\"margin-right: 1em;\">openPump</button><button id='closePumpBtn' width=\"100\" height=\"60\">closePump</button></div></div><script>        document.addEventListener('DOMContentLoaded', function (event) {            var baseHost = document.location.origin;            function fetchUrl(url, cb) {                fetch(url)                    .then(function (response) {                        if (response.status !== 200) {                            cb(response.status, response.statusText);                        } else {                            response.text().then(function (data) {                                cb(200, data);                            }).catch(function (err) {                                cb(-1, err);                            });                        }                    })                    .catch(function (err) {                        cb(-1, err);                    });            };            const openLightBtn = document.getElementById('openLightBtn');            openLightBtn.onclick = () => {                fetchUrl(`${baseHost}/openLight`, function (code, txt) {                    console.log(code + txt);                });            };            const closeLightBtn = document.getElementById('closeLightBtn');            closeLightBtn.onclick = () => {                fetchUrl(`${baseHost}/closeLight`, function (code, txt) {                    console.log(code + txt);                });            };            const openPumpBtn = document.getElementById('openPumpBtn');            openPumpBtn.onclick = () => {                fetchUrl(`${baseHost}/openPump`, function (code, txt) {                    console.log(code + txt);                });            };            const closePumpBtn = document.getElementById('closePumpBtn');            closePumpBtn.onclick = () => {                fetchUrl(`${baseHost}/closePump`, function (code, txt) {                    console.log(code + txt);                });            };            const timeInput = document.getElementById('timeInput');            const repeatInput = document.getElementById('repeatInput');            const confirmBtn = document.getElementById('confirmBtn');            confirmBtn.onclick = () => {                fetchUrl(`${baseHost}/confirm?time=${timeInput.value}&repeat=${repeatInput.value}`, function (code, txt) {                    console.log(code + txt);                });            }        })</script></body></html>"