#define HTML "<!DOCTYPE html><html lang=\"en\" dir=\"ltr\"><head><meta charset=\"utf-8\"><title>Auto-Watering</title><style>        #button_style {            border: 1px solid red;        }</style><meta name=\"viewport\"        content=\"width=device-width, initial-scale=1.0, minimum-scale=1.0, maximum-scale=1.0, user-scalable=no\"><meta name=\"apple-touch-fullscreen\" content=\"YES\"><meta name=\"apple-mobile-web-app-capable\" content=\"yes\"></head><body><datalist id=\"timeInputs\"><option value=\"now\"></option><option value=\"00:00\"></option><option value=\"09:00\"></option><option value=\"12:00\"></option><option value=\"15:00\"></option><option value=\"18:00\"></option><option value=\"21:00\"></option></datalist><datalist id=\"repeatInputs\"><option value=\"6 hour\"><option value=\"12 hour\"><option value=\"1 day\"><option value=\"2 day\"><option value=\"3 day\"><option value=\"4 day\"><option value=\"5 day\"><option value=\"6 day\"><option value=\"7 day\"></datalist><datalist id=\"waters\"><option value=\"5\"><option value=\"10\"><option value=\"20\"><option value=\"30\"><option value=\"60\"></datalist><div><div style=\"margin: 1em;\"><div style=\"margin-top: 1em;\">                Time<input style=\"margin-left: 1em;\" id=\"timeInput\" list=\"timeInputs\" /></div><div style=\"margin-top: 1em;\">                Repeat<input style=\"margin-left: 1em;\" id=\"repeatInput\" list=\"repeatInputs\" /></div><div style=\"margin-top: 1em;\">                Water<input style=\"margin-left: 1em;\" id=\"waterInput\" list=\"waters\" /></div><button style=\"margin-top: 1em;\" id='confirmBtn' width=\"100\" height=\"60\" style=\"margin-right: 1em;\">confirm</button></div><div style=\"margin: 1em;\"><button id='openLightBtn' width=\"100\" height=\"60\" style=\"margin-right: 1em;\">openLight</button><button id='closeLightBtn' width=\"100\" height=\"60\">closeLight</button></div><div style=\"margin: 1em;\"><button id='openPumpBtn' width=\"100\" height=\"60\" style=\"margin-right: 1em;\">openPump</button><button id='closePumpBtn' width=\"100\" height=\"60\">closePump</button></div></div><script>        document.addEventListener('DOMContentLoaded', function (event) {            var baseHost = document.location.origin;            function fetchUrl(url, cb) {                fetch(url, {                    method: 'GET',                    mode: 'cors'                })                    .then(function (response) {                        if (response.status !== 200) {                            cb(response.status, response.statusText);                        } else {                            response.text().then(function (data) {                                cb(200, data);                            }).catch(function (err) {                                cb(-1, err);                            });                        }                    })                    .catch(function (err) {                        cb(-1, err);                    });            };            const openLightBtn = document.getElementById('openLightBtn');            openLightBtn.onclick = () => {                fetchUrl(`${baseHost}/openLight`, function (code, txt) {                    console.log(code + txt);                });            };            const closeLightBtn = document.getElementById('closeLightBtn');            closeLightBtn.onclick = () => {                fetchUrl(`${baseHost}/closeLight`, function (code, txt) {                    console.log(code + txt);                });            };            const openPumpBtn = document.getElementById('openPumpBtn');            openPumpBtn.onclick = () => {                fetchUrl(`${baseHost}/openPump`, function (code, txt) {                    console.log(code + txt);                });            };            const closePumpBtn = document.getElementById('closePumpBtn');            closePumpBtn.onclick = () => {                fetchUrl(`${baseHost}/closePump`, function (code, txt) {                    console.log(code + txt);                });            };            const timeInput = document.getElementById('timeInput');            const repeatInput = document.getElementById('repeatInput');            const confirmBtn = document.getElementById('confirmBtn');            const waterInput = document.getElementById('waterInput');            confirmBtn.onclick = () => {                fetchUrl(`${baseHost}/confirm?time=${timeInput.value}&repeat=${repeatInput.value}&water=${waterInput.value}`, function (code, txt) {                    console.log(code + txt);                });            }        })</script></body></html>"